mapper.insert:
  insert into usercocktail
   (
    usercocktailseq
    ,useremail
    ,usercocktailname
    ,USERCOCKTAILNAME_EN
    ,userIngredient
    ,userreceipe
    ,savefilename
    ,originalfilename
    ,usercolorvalue
   )
	values(
			usercocktailseq.nextval
			,#{useremail}
			,#{usercocktailname}
			,'NO ENG NAME'
			,#{userIngredient}
			,'레시피가 없습니다.'
			,#{savefilename}
			,#{originalfilename}
			,#{usercolorvalue}
	)

mapper.update:
  update
  	usercocktail
  set
  		usercocktailname=#{usercocktailname}
		,usercocktailname_en=#{usercocktailname_en}
		,useralcole=#{useralcole}
		,userliqueur=#{userliqueur}
		,usermaterial=#{usermaterial}
		,userreceipe=#{userreceipe}
		,usercolorvalue=#{usercolorvalue}
  where
  		usercocktailseq=#{userCocktailSeq}
