mapper.insert:
  insert into cocktail2
	(
	cocktailSeq,
	cocktailName,
	cocktailNameEn,
	Ingredient,
	recipe,
	imageRink,
	colorValue
	)
	values(
			cocktailSeq.nextval
			,#{cocktailName}
			,#{cocktailNameEn}
			,#{Ingredient}
			,#{recipe}
			,#{imageRink}
			,#{colorValue} 
	)

mapper.update:
  update
  	cocktail2
  set
  		cocktailName=#{cocktailName}
		,ccocktailNameEn=#{cocktailNameEn}
		,Ingredient=#{Ingredient}
		,recipe=#{recipe}
		,imageRink=#{imageRink}
		,colorValue=#{colorValue}
  where
  		cocktailSeq=#{cocktailSeq}
