mapper.insert:
  insert into usercocktail
   (
    usercocktailseq
    ,useremail
    ,usercocktailname
    ,usercocktailname_en
    ,userIngredient
    ,userreceipe
    ,savefilename
    ,originalfilename
    ,usercolorvalue
   )
	values(
			usercocktailseq.nextval
			,#{useremail}
			,#{usercocktailname}
			,#{usercocktailname_en}
			,#{useralcole}
			,#{userliqueur}
			,#{usermaterial}
			,#{userreceipe}
			,#{savefilename}
			,#{originalfilename}
			,#{usercolorvalue}
	)

mapper.update:
  update
  	usercocktail
  set
  		usercocktailname=#{usercocktailname}
		,usercocktailname_en=#{usercocktailname_en}
		,useralcole=#{useralcole}
		,userliqueur=#{userliqueur}
		,usermaterial=#{usermaterial}
		,userreceipe=#{userreceipe}
		,usercolorvalue=#{usercolorvalue}
  where
  		usercocktailseq=#{userCocktailSeq}
